import React,{useState} from "react";
import './Expenses.css'
import ExpenseItems from "./ExpenseItems";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
   const [filteredYear, setFilteredYear] = useState('2020')
   const yearChangeHandler = (selectedYear) => {
      setFilteredYear(selectedYear)
   }
   return (
      <div>


         <Card className="expenses">
         <ExpensesFilter onChangeFilter={yearChangeHandler}/>
            <ExpenseItems title={props.items[0].title}
            amount={props.items[0].amount} date={props.items[0].date}/>
            <ExpenseItems title={props.items[1].title}
            amount={props.items[1].amount} date={props.items[1].date}/>
            <ExpenseItems title={props.items[2].title}
            amount={props.items[2].amount} date={props.items[2].date}/>
            <ExpenseItems title={props.items[3].title}
            amount={props.items[3].amount} date={props.items[3].date}/>
         </Card>  
   </div>    
   )
}

export default Expenses
