import React,{useState} from 'react';
import './App.css';
import TaskInput from './components/TasksData/TaskInput/TaskInput';
import TaskList from './components/TasksData/TaskList/TaskList';

const App = () => {
  const [task, setTask] = useState([
    { text: 'Do all exercises!', id: 'g1' },
    { text: 'Finish the course!', id: 'g2' }
  ]);

  const addTaskHandler = inputValue => {
    setTask(prevTasks => {
      const updatedTasks = [...prevTasks];
      updatedTasks.unshift({ text: inputValue, id: Math.random().toString() });
      return updatedTasks;
    });
  };

  const deleteItemHandler = taskId => {
    setTask(prevTasks => {
      const updatedTasks = prevTasks.filter(task => task.id !== taskId);
      return updatedTasks;
    });
  };

  let content = (
    <p style={{ textAlign: 'center' }}>No goals found. Maybe add one?</p>
  );

  if (task.length > 0) {
    content = (
      <TaskList items={task} onDeleteItem={deleteItemHandler} />
    );
  }

  return (
    <div>
      <section id="goal-form">
        <TaskInput onAddTask={addTaskHandler} />
      </section>
      <section id="goals">
        {content}
      </section>
    </div>
  );
};

export default App;
